import logo from "../images/logo.svg";
import { Link, withRouter, useNavigate } from "react-router-dom";
import { React, useEffect, useState } from "react";

function Login() {
  const navigate = useNavigate();
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     email: "",
  //     password: "",
  //   };
  //   this.handleChange = this.handleChange.bind(this);
  //   this.handleSubmit = this.handleSubmit.bind(this);
  // }

  const [userValue, setUserValue] = useState({
    email: "",
    password: "",
  });

  useEffect(() => {
    navigate("/movies");
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(value);
    setUserValue({
      ...userValue,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log("ggg");
    // this.props.setLogin(true);
    // console.log("ggg");
    // this.props.setEmail(this.state.email);
    // this.props.setName(this.state.name);
  };

  return (
    <section className="registration">
      <header className="registration__header">
        <Link to="/" className="registration__logo">
          <img src={logo} alt="логотип" />
        </Link>
      </header>
      <main className="registration__main">
        <h1 className="registration__title">Рады видеть!</h1>
        <form>
          <label className="registration__label">
            <p className="registration__text">E-mail</p>
            <input
              name="email"
              type="email"
              className="registration__input"
              id="Email"
              placeholder=""
              required
              onChange={this.handleChange}
              value={this.state.email}
            />
            <span id="registration__error" className="registration__error">
              скрытый текст
            </span>
          </label>
          <label className="registration__label">
            <p className="registration__text">Пароль</p>
            <input
              name="password"
              type="password"
              className="registration__input"
              id="password"
              placeholder=""
              required
              onChange={this.handleChange}
              value={this.state.password}
            />
            <span id="registration__error" className="registration__error">
              скрытый текст
            </span>
          </label>
          <button className="registration__save-button" type="button">
            Войти
          </button>
        </form>
      </main>
      <footer>
        <Link to="/signup" className="registration__content-links">
          <p className="registration__content">Ещё не зарегистрированы?</p>
          <p className="registration__content">Регистрация</p>
        </Link>
      </footer>
    </section>
  );
}

export default Login;
