const favoriteButton = (savedMovies, obj, MoviesToFavorite) => {
  if (savedMovies.some((i) => i.movieId === obj.id)) {
    return (
      <button
        className={`films__ico-block_active`}
        onClick={() => {
          MoviesToFavorite(obj);
        }}
      ></button>
    );
  } else {
    return (
      <button
        className={`films__ico-block`}
        onClick={() => {
          MoviesToFavorite(obj);
        }}
      ></button>
    );
  }
};

export default favoriteButton;
