// import './index.css';
import Main from "../components/Main";
import Films from "../components/Films";
import SavedFilms from "../components/SavedFilms";
import Registration from "../components/Registration";
import Login from "../components/Login";
import Error from "../components/Error";
import Account from "../components/Account";
import ProtectedRoute from "../components/ProtectedRoute";
import { React, useEffect, useState } from "react";
import { Routes, Route, Redirect, useNavigate } from "react-router-dom";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import { api } from "../utils/MainApi";

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setСurrentUser] = useState([]);
  const [movies, setMovies] = useState([]);
  const [savedMovies, setSavedMovies] = useState([]);
  const [localFilms, setLocalFilms] = useState([]);
  const [isSideBarOpen, setSideBarOpen] = useState(false);

  useEffect(() => {
    if (window.screen.availWidth <= 1024) {
      isSideBarOpen(true);
    }
  }, []);

  const SearchFilter = () => {
    // if (JSON.parse(localStorage.getItem("isShort") === "true")) {
    //   setMovies((movies) => movies.filter((item) => item.duration <= 40));
    // } else if (localStorage.getItem("SearchFilm") !== null) {
    //   setMovies([JSON.parse(localStorage.getItem("SearchFilm"))]);
    // } else if (
    //   localStorage.getItem("SearchFilm") !== null &&
    //   JSON.parse(localStorage.getItem("isShort") === "true")
    // ) {
    //   setMovies((movies) => movies.filter((item) => item.duration <= 40));
    //   JSON.parse(localStorage.getItem("isShort") === "true");
    // } else {
    //   setMovies(JSON.parse(localStorage.getItem("AllFilms")));
    // }
    if (localStorage.getItem("SearchFilm") === null) {
      if (isShortFilms === true) {
        setMovies((movies) => movies.filter((item) => item.duration <= 40));
      } else {
        props.setMovies(JSON.parse(localStorage.getItem("AllFilms")));
      }
    } else {
      if (isShortFilms === false) {
        setMovies((movies) => movies.filter((item) => item.duration <= 40));
      } else {
        props.setMovies(JSON.parse(localStorage.getItem("AllFilms")));
        props.movies.map((obj) => {
          obj.nameRU.split(" ").some((movie) => {
            if (searchContent.movie.length === 0) {
              localStorage.removeItem("SearchFilm");
              props.setMovies(JSON.parse(localStorage.getItem("AllFilms")));
            } else if (
              movie.toLowerCase() === searchContent.movie.toLowerCase()
            ) {
              localStorage.setItem("SearchFilm", JSON.stringify(obj));
              props.setMovies([JSON.parse(localStorage.getItem("SearchFilm"))]);
            }
          });
        });
      }
    }
  };

  return (
    <div className="App" lang="ru">
      <CurrentUserContext.Provider value={currentUser}>
        <Routes>
          <Route path="/" element={<Main loggedIn={loggedIn} />}></Route>
          <Route
            path="/signup"
            element={
              <Registration
                setСurrentUser={setСurrentUser}
                setMovies={setMovies}
                setLoggedIn={setLoggedIn}
                setSavedMovies={setSavedMovies}
              />
            }
          ></Route>
          <Route
            path="/signin"
            element={
              <Login
                SearchFilter={SearchFilter}
                setLocalFilms={setLocalFilms}
                setСurrentUser={setСurrentUser}
                setMovies={setMovies}
                setLoggedIn={setLoggedIn}
                setSavedMovies={setSavedMovies}
              />
            }
          ></Route>
          <Route path="/error" element={<Error />}></Route>
          <Route
            path="/movies"
            element={
              <ProtectedRoute
                element={
                  <Films
                    SearchFilter={SearchFilter}
                    setSavedMovies={setSavedMovies}
                    localFilms={localFilms}
                    setMovies={setMovies}
                    // movies2={movies}
                    movies={movies}
                    savedMovies={savedMovies}
                  />
                }
                loggedIn={loggedIn}
              />
            }
          ></Route>
          <Route
            path="/saved-movies"
            element={
              <ProtectedRoute
                element={
                  <SavedFilms
                    setSavedMovies={setSavedMovies}
                    savedMovies={savedMovies}
                  />
                }
                loggedIn={loggedIn}
              />
            }
          ></Route>
          <Route
            path="/profile"
            element={
              <ProtectedRoute
                element={
                  <Account
                    setLoggedIn={setLoggedIn}
                    setСurrentUser={setСurrentUser}
                  />
                }
                loggedIn={loggedIn}
              />
            }
          ></Route>
        </Routes>
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
