// import './index.css';
import Main from "../components/Main";
import Films from "../components/Films";
import SavedFilms from "../components/SavedFilms";
import Registration from "../components/Registration";
import Login from "../components/Login";
import Error from "../components/Error";
import Account from "../components/Account";
import { React, useEffect, useState } from "react";
import { Routes, Route, Redirect, useNavigate } from "react-router-dom";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import { api } from "../utils/Api";

function App() {
  const [currentUser, setСurrentUser] = useState([]);
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    Promise.all([api.getProfile(), api.getMovies()])
      .then(([infoResult, moviesResult]) => {
        setСurrentUser(infoResult);
        setMovies(moviesResult.reverse());
        console.log(moviesResult);
      })
      .catch((err) => {
        console.log(`Вы неавторизованы ${err}`);
      });
  }, []);

  return (
    <div className="App" lang="ru">
      <CurrentUserContext.Provider value={currentUser}>
        <Routes>
          <Route path="/" element={<Main />}></Route>
          <Route path="/movies" movies={movies} element={<Films />}></Route>
          <Route path="/saved-movies" element={<SavedFilms />}></Route>
          <Route path="/signup" element={<Registration />}></Route>
          <Route path="/signin" element={<Login />}></Route>
          <Route path="/error" element={<Error />}></Route>
          <Route path="/profile" element={<Account />}></Route>
        </Routes>
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
