  //     ...userValue,
  //     [name]: value,
  //   });
  // };

  return (
    <section>
      <header>
        <nav className="navbar">
          <Link to="/" className="navbar__avatar">
            <img src={logo} className="profile__avatar-img" alt="Ð»Ð¾Ð³Ð¾ÑÐ¸Ð¿" />
