import logo from "../images/logo.svg";
import { Link } from "react-router-dom";
import React from "react";
class Registration extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      password: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <section className="registration">
        <header className="registration__header">
          <Link to="/" className="registration__logo">
            <img src={logo} alt="логотип" />
          </Link>
        </header>
        <main className="registration__main">
          <h1 className="registration__title">Добро пожаловать!</h1>
          <label className="registration__label">
            <p className="registration__text">Имя</p>
            <input
              name="name"
              className="registration__input"
              id="url"
              placeholder=""
              required
              onChange={this.handleChange}
              value={this.state.name}
            />
            <span id="registration__error" className="registration__error">
              скрытый текст
            </span>
          </label>
          <label className="registration__label">
            <p className="registration__text">E-mail</p>
            <input
              name="email"
              type="email"
              className="registration__input"
              id="url"
              placeholder=""
              required
              onChange={this.handleChange}
              value={this.state.email}
            />
            <span id="registration__error" className="registration__error">
              скрытый текст
            </span>
          </label>
          <label className="registration__label">
            <p className="registration__text">Пароль</p>
            <input
              name="password"
              type="password"
              className="registration__input"
              id="url"
              placeholder=""
              required
              onChange={this.handleChange}
              value={this.state.password}
            />
            <span id="registration__error" className="registration__error">
              скрытый текст
            </span>
          </label>
          <button
            className="registration__save-button"
            onSubmit={this.handleSubmit}
          >
            Зарегистрироваться
          </button>
        </main>
        <footer>
          <Link to="/signin" className="registration__content-links">
            <p className="registration__content">Уже зарегистрированы?</p>
            <p className="registration__content">Войти</p>
          </Link>
        </footer>
      </section>
    );
  }
}

export default Registration;
